    margin-top: 1rem;
  }

  .progress-step {
    flex: 1;
    text-align: center;
    position: relative;
    padding: 0 0.5rem;
  }

  .progress-step:not(:last-child)::after {
    content: '';
    position: absolute;
    top: 1.25rem;
    right: -50%;
    width: 100%;
    height: 2px;
    background-color: var(--progress-inactive);
    z-index: 1;
  }

  .progress-step.active:not(:last-child)::after,
  .progress-step.complete:not(:last-child)::after {
    background-color: var(--progress-active);
  }

  .step-indicator {
    width: 2.5rem;
    height: 2.5rem;
    border-radius: 50%;
    background-color: var(--progress-inactive);
    color: var(--white);
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 0.5rem;
    position: relative;
    z-index: 2;
    font-weight: bold;
    transition: background-color 0.3s;
  }

  .progress-step.active .step-indicator {
    background-color: var(--progress-active);
  }

  .progress-step.complete .step-indicator {
    background-color: var(--progress-complete);
  }

  .step-label {
    font-weight: 500;
    margin-bottom: 0.25rem;
  }

  .step-status {
    font-size: 0.8rem;
    color: var(--dark-gray);
  }

  .job-content {
    padding: 1.5rem;
  }

  .process-section,
  .bottlenecks-section,
  .improvements-section {
    margin-bottom: 2rem;
    padding-bottom: 2rem;
    border-bottom: 1px solid var(--border-color);
  }

  .improvements-section {
    border-bottom: none;
    margin-bottom: 0;
    padding-bottom: 0;
  }

  .process-section h3,
  .bottlenecks-section h3,
  .improvements-section h3 {
    color: var(--stanford-red);
    margin-bottom: 1rem;
    font-size: 1.25rem;
  }

  .text-area {
    background-color: var(--light-gray);
    padding: 1.5rem;
    border-radius: 6px;
    margin: 1rem 0;
    min-height: 150px;
    position: relative;
    white-space: pre-wrap;
    font-family: monospace;
    line-height: 1.5;
  }

  .download-button {
    position: absolute;
    bottom: 0.75rem;
    right: 0.75rem;
    background-color: transparent;
    border: 1px solid var(--stanford-red);
    color: var(--stanford-red);
    padding: 0.25rem 0.75rem;
    border-radius: 4px;
    cursor: pointer;
    transition: all 0.2s;
  }

  .download-button:hover {
    background-color: var(--stanford-red);
    color: var(--white);
  }

  .action-button {
    background-color: var(--stanford-red);
    color: var(--white);
    border: none;
    padding: 0.5rem 1rem;
    border-radius: 4px;
    cursor: pointer;
    margin: 1rem 0;
    transition: all 0.2s;
  }

  .action-button:hover {
    background-color: #7a1010;
  }

  .action-button:disabled {
    background-color: var(--medium-gray);
    cursor: not-allowed;
  }

  .processing-message,
  .waiting-message {
    padding: 1.5rem;
    background-color: var(--light-gray);
    border-radius: 6px;
    color: var(--dark-gray);
    text-align: center;
    font-style: italic;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .upload-status {
    margin-top: 1rem;
    padding: 0.5rem;
    border-radius: 4px;
    font-weight: 500;
    color: var(--stanford-red);
  }

  .loading {
    margin-top: 1rem;
    color: var(--dark-gray);
    font-style: italic;
  }

  @media (max-width: 768px) {
    .dashboard-content {
      flex-direction: column;
    }

    .sidebar {
      width: 100%;
      max-height: 300px;
    }

    .main-content {
      padding: 1rem;
    }

    .job-header {
      flex-direction: column;
      align-items: flex-start;
      gap: 1rem;
    }

    .progress-steps {
      flex-direction: column;
      gap: 1.5rem;
    }

    .progress-step:not(:last-child)::after {
      top: auto;
      bottom: -1rem;
      right: auto;
      left: 1.25rem;
      width: 2px;
      height: 1rem;
    }
  }
`;

